import '../styleSheets/contact.css'
import { useState } from "react";
import { splashPics } from "./data";
import { socialsObj } from "./data"

function Contact(){

let content =
<section className="social-icons">
{socialsObj.map(pic => <img src={pic.image} alt={pic.alt} className={pic.className}/>)}
</section>

const [display, setDisplay] = useState('');

const displayClickHandler = () =>{
  !display ?
  setDisplay(content):
  setDisplay('')
  };

  let contactImage = <img src={splashPics[1]} alt="block" className="contact-splash-img" id="left"/>


return(
    <section>
    <div id="resume" className={!display ? 'contact-card' : 'contact-card-active'} onClick={displayClickHandler}>
        {contactImage}
        {display}
        <p>Connect</p>
        {content}
    </div>
    <div class="contact"></div> 
    </section>
    )
}

export default Contact;
